import machineimport socketimport ureimport mp3def play_track(track):  if track is None:    track=1  mp3.play_track(int(track))  return "track %s" % (int(track))def play_next():  mp3.next()  return "done"def play_prev():  mp3.previous()  return "done"  def pause_play():  mp3.pause()  return "Paused"def resume_play():  mp3.resume()  return "resumed"mp3.play()